import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

import { Cliente } from './cliente';

@Component({
  selector: 'app-clientes',
  templateUrl: './clientes.component.html',
  styleUrls: ['./clientes.component.css'],
})
export class ClientesComponent implements OnInit {
  constructor(private http: HttpClient) {}

  REST_API_SERVER = 'https://localhost:7245/api/Cliente';

  clientes = [];
  cliente!: Cliente;
  mensagem!: string;

  ngOnInit(): void {
    this.cliente = new Cliente();
    this.carregarLista();
  }

  carregarLista() {
    this.http.get(this.REST_API_SERVER).subscribe((data: Cliente[]) => {
      this.clientes = data;
    });
  }

  Salvar() {
    if (!this.cliente.id || this.cliente.id == 0) {
      this.http
        .post(this.REST_API_SERVER, this.cliente)
        .subscribe((cliente: Cliente) => {
          this.cliente = new Cliente();
          this.mensagem = 'Usuário Cadastrado com sucesso!';
          setTimeout(() => {
            this.mensagem = undefined;
          }, 3000);
          this.carregarLista();
        });
    } else {
      this.http
        .put(`${this.REST_API_SERVER}/ ${this.cliente.id}`, this.cliente)
        .subscribe((cliente: Cliente) => {
          this.cliente = new Cliente();
          this.mensagem = 'Usuário atualizado com sucesso!';
          setTimeout(() => {
            this.mensagem = undefined;
          }, 3000);
          this.carregarLista();
        });
    }
  }

  Alterar(_cliente: Cliente) {
    this.cliente = _cliente;
  }

  Excluir(cliente: Cliente) {
    if (confirm('Tem certeza que deseja excluir o usuário?')) {
      this.http
        .delete(`${this.REST_API_SERVER}/${cliente.id}`)
        .subscribe(() => {
          this.mensagem = 'Usuário excluído com sucesso!';
          setTimeout(() => {
            this.mensagem = undefined;
          }, 3000);
          this.carregarLista();
        });
    }
  }
}
<form style="margin-top: 20px;">
  <legend>Cliente:</legend>
  <div class="form-group">
    <label for="nome">Nome</label>
    <input type="text" [(ngModel)]="cliente.nome" class="form-control" id="nome" name="nome" placeholder="Nome do cliente">
    <small id="emailHelp" class="form-text text-muted">Campo obrigatório.</small>
  </div>
  <div class="form-group">
    <label for="endereco">Endereço</label>
    <input type="text" [(ngModel)]="cliente.endereco" class="form-control" id="endereco" name="endereco" placeholder="Endereço do cliente">
  </div>
  <div class="form-group">
    <label for="telefone">Telefone</label>
    <input type="text" [(ngModel)]="cliente.telefone" class="form-control" id="telefone" name="telefone" placeholder="Telefone do cliente">
    <small id="emailHelp" class="form-text text-muted">Campo obrigatório.</small>
  </div>
  <button type="button" (click)="Salvar()" [disabled]="!cliente.nome || !cliente.telefone" class="btn btn-success">Salvar</button>
</form>

<div class="alert alert-success" *ngIf="mensagem" style="margin-top: 10px">
  {{ mensagem }}
</div>

<br />

<table class="table">
  <thead>
    <tr>
      <th scope="col">ID</th>
      <th scope="col">NOME</th>
      <th scope="col">ENDEREÇO</th>
      <th scope="col">TELEFONE</th>
      <th scope="col"></th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let cliente of clientes">
      <td>{{ cliente.id }}</td>
      <td>{{ cliente.nome }}</td>
      <td>{{ cliente.endereco }}</td>
      <td>{{ cliente.telefone }}</td>
      <td>
        <button class="btn btn-primary" style="margin-right: 10px;" (click)="Alterar(cliente)">Alterar</button>
        <button class="btn btn-danger" (click)="Excluir(cliente)">Excluir</button>
      </td>
    </tr>
  </tbody>
</table>
